mapper.select:
SELECT 

FROM wx_msg a

WHERE a.id = #{id}

SELECT 

FROM wx_msg a


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.accountid = #{accountid} 

	 AND a.msgtype=#{msgtype}

		AND a.content LIKE 
			'%'||#{content}||'%'
			'%'+#{content}+'%'
			concat('%',#{content},'%')




		ORDER BY ${page.orderBy}

SELECT 

FROM wx_msg a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE wx_msg SET 	

	media_id = #{mediaId},

	modify_time = #{modifyTime},
	content = #{content},
	title = #{title}
WHERE id = #{id}

UPDATE wx_msg SET 
	del_flag = '1'
WHERE id = #{id}
